const Noop = require('sequelize-test-helpers/src/types/Noop')
const NumericType = require('sequelize-test-helpers/src/types/NumericType')
const StringType = require('sequelize-test-helpers/src/types/StringType')
const basicTypes = require('sequelize-test-helpers/src/constants/basicTypes')
const numericTypes = require('sequelize-test-helpers/src/constants/numericTypes')
const stringTypes = require('sequelize-test-helpers/src/constants/stringTypes')
const deferrables = require('sequelize-test-helpers/src/constants/deferrables')

const basicDataTypes = basicTypes.reduce((acc, elem) => {
  acc[elem] = Noop
  return acc
}, {})

const numericDataTypes = numericTypes.reduce((acc, elem) => {
  acc[elem] = NumericType
  return acc
}, basicDataTypes)

const dataTypes = stringTypes.reduce((acc, elem) => {
  acc[elem] = StringType
  return acc
}, numericDataTypes)

const Deferrable = deferrables.reduce((acc, elem) => {
  acc[elem] = elem
  return acc
}, {})

module.exports = {
  ...dataTypes,
  Deferrable
}
